class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$("#post-comments-"+e).prepend(o),t.deleteComment($(".delete-comment-button",o)),$(`#post-${e}-comments-form`)[0].reset(),new ToggleLike($(" .toggle-like-button",o)),flash_msg("success","Comment Published!")},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<div id="comment-${e._id}">\n        <div class="dropdown">\n            <a class="float-right" href="" id="more_options_${e._id}" data-toggle="dropdown"\n                aria-haspopup="true" aria-expanded="false">\n                <i class="fas fa-ellipsis-h"></i>\n            </a>\n            <div class="dropdown-menu" aria-labelledby="more_options_${e._id}">\n                <a class="dropdown-item delete-comment-button" href="/comments/destroy/${e._id}"><i\n                        class="fas fa-trash-alt"></i>\n                    Delete</a>\n            </div>\n        </div>\n        <b>${e.user.name}</b>\n        <p>\n            ${e.content}\n        </p>\n        <div class="align-middle action-buttons">\n            \x3c!-- like button on post --\x3e\n            <a href="/likes/toggle/?id=${e._id}&type=Comment" class="toggle-like-button"\n                data-likes="0"><i class="far fa-heart"></i> <span>0</span> </a> &nbsp\n            \x3c!-- comment button on post --\x3e\n            <a data-toggle="collapse" href="#collapse${e._id}" role="button" aria-expanded="false"\n            aria-controls="collapse${e._id}"><i class="far fa-comment"></i></a>&nbsp\n        </div>\n        <hr>\n    </div>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),flash_msg("success","Comment Deleted!")},error:function(e){console.log(e.responseText)}})}))}}